import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { journalAPI, storiesAPI, practiceAPI } from '../services/api';
import { 
  User, 
  Mail, 
  Calendar, 
  BookOpen, 
  MessageSquare, 
  Play, 
  Star,
  TrendingUp,
  Target,
  Clock,
  Award,
  BarChart3,
  Edit,
  Save,
  X
} from 'lucide-react';
import { format } from 'date-fns';
import toast from 'react-hot-toast';

const Profile = () => {
  const { user, logout } = useAuth();
  const [stats, setStats] = useState({
    totalEntries: 0,
    totalStories: 0,
    totalPracticeSessions: 0,
    avgRating: 0,
    storiesTold: 0,
    streak: 0
  });
  const [recentActivity, setRecentActivity] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingName, setEditingName] = useState(false);
  const [newName, setNewName] = useState('');

  useEffect(() => {
    if (user) {
      loadProfileData();
      setNewName(user.name);
    }
  }, [user]);

  const loadProfileData = async () => {
    try {
      const [entriesResponse, storiesResponse, practiceResponse] = await Promise.all([
        journalAPI.getEntries(1, 100),
        storiesAPI.getStories(1, 100),
        practiceAPI.getStats()
      ]);

      const entries = entriesResponse.data.entries || [];
      const stories = storiesResponse.data.stories || [];
      const practiceStats = practiceResponse.data.stats || {};

      // Calculate additional stats
      const storiesTold = stories.reduce((sum, story) => sum + (story.times_told || 0), 0);
      const totalWords = entries.reduce((sum, entry) => sum + entry.content.length, 0);

      setStats({
        totalEntries: entries.length,
        totalStories: stories.length,
        totalPracticeSessions: practiceStats.totalSessions || 0,
        avgRating: practiceStats.avgRating || 0,
        storiesTold,
        totalWords,
        streak: calculateStreak(entries)
      });

      // Create recent activity
      const activity = [
        ...entries.slice(0, 3).map(entry => ({
          type: 'journal',
          title: 'Journal Entry',
          description: entry.content.substring(0, 50) + '...',
          date: entry.created_at,
          icon: BookOpen
        })),
        ...stories.slice(0, 3).map(story => ({
          type: 'story',
          title: 'Story Created',
          description: story.title,
          date: story.created_at,
          icon: MessageSquare
        }))
      ].sort((a, b) => new Date(b.date) - new Date(a.date)).slice(0, 5);

      setRecentActivity(activity);
    } catch (error) {
      console.error('Error loading profile data:', error);
      toast.error('Failed to load profile data');
    } finally {
      setLoading(false);
    }
  };

  const calculateStreak = (entries) => {
    if (entries.length === 0) return 0;

    const today = new Date();
    const sortedEntries = entries
      .map(entry => new Date(entry.created_at))
      .sort((a, b) => b - a);

    let streak = 0;
    let currentDate = new Date(today);

    for (const entryDate of sortedEntries) {
      const entryDay = new Date(entryDate.getFullYear(), entryDate.getMonth(), entryDate.getDate());
      const currentDay = new Date(currentDate.getFullYear(), currentDate.getMonth(), currentDate.getDate());

      if (entryDay.getTime() === currentDay.getTime()) {
        streak++;
        currentDate.setDate(currentDate.getDate() - 1);
      } else if (entryDay.getTime() < currentDay.getTime()) {
        break;
      }
    }

    return streak;
  };

  const handleSaveName = async () => {
    if (!newName.trim()) {
      toast.error('Name cannot be empty');
      return;
    }

    try {
      // In a real app, you would call an API to update the user's name
      toast.success('Name updated successfully');
      setEditingName(false);
    } catch (error) {
      console.error('Error updating name:', error);
      toast.error('Failed to update name');
    }
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      logout();
    }
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-6"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="h-64 bg-gray-200 rounded-lg"></div>
            <div className="h-64 bg-gray-200 rounded-lg"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Profile</h1>
        <p className="text-gray-600 mt-1">
          Your conversation journey and progress
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Profile Info */}
        <div className="lg:col-span-1">
          <div className="card">
            <div className="text-center">
              <div className="w-24 h-24 bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full flex items-center justify-center mx-auto mb-4">
                <User className="w-12 h-12 text-white" />
              </div>

              <div className="mb-4">
                {editingName ? (
                  <div className="flex items-center justify-center space-x-2">
                    <input
                      type="text"
                      value={newName}
                      onChange={(e) => setNewName(e.target.value)}
                      className="text-xl font-semibold text-gray-900 text-center border-b border-gray-300 focus:border-primary-500 focus:outline-none"
                    />
                    <button
                      onClick={handleSaveName}
                      className="p-1 text-green-600 hover:text-green-700"
                    >
                      <Save className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => {
                        setEditingName(false);
                        setNewName(user.name);
                      }}
                      className="p-1 text-red-600 hover:text-red-700"
                    >
                      <X className="w-4 h-4" />
                    </button>
                  </div>
                ) : (
                  <div className="flex items-center justify-center space-x-2">
                    <h2 className="text-xl font-semibold text-gray-900">{user.name}</h2>
                    <button
                      onClick={() => setEditingName(true)}
                      className="p-1 text-gray-400 hover:text-gray-600"
                    >
                      <Edit className="w-4 h-4" />
                    </button>
                  </div>
                )}
              </div>

              <div className="space-y-2 text-sm text-gray-600">
                <div className="flex items-center justify-center">
                  <Mail className="w-4 h-4 mr-2" />
                  {user.email}
                </div>
                <div className="flex items-center justify-center">
                  <Calendar className="w-4 h-4 mr-2" />
                  Joined {format(new Date(user.created_at), 'MMM yyyy')}
                </div>
              </div>
            </div>
          </div>

          {/* Quick Stats */}
          <div className="card mt-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Stats</h3>
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Current Streak</span>
                <span className="font-semibold text-primary-600">{stats.streak} days</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Stories Told</span>
                <span className="font-semibold text-green-600">{stats.storiesTold}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Avg Rating</span>
                <span className="font-semibold text-yellow-600">
                  {stats.avgRating ? stats.avgRating.toFixed(1) : '0.0'}/10
                </span>
              </div>
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="lg:col-span-2 space-y-6">
          {/* Stats Grid */}
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="card text-center">
              <BookOpen className="w-8 h-8 text-blue-600 mx-auto mb-2" />
              <div className="text-2xl font-bold text-gray-900">{stats.totalEntries}</div>
              <div className="text-sm text-gray-600">Journal Entries</div>
            </div>

            <div className="card text-center">
              <MessageSquare className="w-8 h-8 text-purple-600 mx-auto mb-2" />
              <div className="text-2xl font-bold text-gray-900">{stats.totalStories}</div>
              <div className="text-sm text-gray-600">Stories Created</div>
            </div>

            <div className="card text-center">
              <Play className="w-8 h-8 text-green-600 mx-auto mb-2" />
              <div className="text-2xl font-bold text-gray-900">{stats.totalPracticeSessions}</div>
              <div className="text-sm text-gray-600">Practice Sessions</div>
            </div>

            <div className="card text-center">
              <Star className="w-8 h-8 text-yellow-600 mx-auto mb-2" />
              <div className="text-2xl font-bold text-gray-900">
                {stats.avgRating ? stats.avgRating.toFixed(1) : '0.0'}
              </div>
              <div className="text-sm text-gray-600">Avg Rating</div>
            </div>
          </div>

          {/* Progress Overview */}
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Progress Overview</h3>
            <div className="space-y-4">
              <div>
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium text-gray-700">Journal Entries</span>
                  <span className="text-sm text-gray-600">{stats.totalEntries}</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${Math.min((stats.totalEntries / 50) * 100, 100)}%` }}
                  ></div>
                </div>
              </div>

              <div>
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium text-gray-700">Stories Created</span>
                  <span className="text-sm text-gray-600">{stats.totalStories}</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-purple-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${Math.min((stats.totalStories / 20) * 100, 100)}%` }}
                  ></div>
                </div>
              </div>

              <div>
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium text-gray-700">Practice Sessions</span>
                  <span className="text-sm text-gray-600">{stats.totalPracticeSessions}</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-green-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${Math.min((stats.totalPracticeSessions / 30) * 100, 100)}%` }}
                  ></div>
                </div>
              </div>
            </div>
          </div>

          {/* Recent Activity */}
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Recent Activity</h3>
            {recentActivity.length > 0 ? (
              <div className="space-y-3">
                {recentActivity.map((activity, index) => {
                  const Icon = activity.icon;
                  return (
                    <div key={index} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                      <div className="p-2 bg-white rounded-lg">
                        <Icon className="w-4 h-4 text-gray-600" />
                      </div>
                      <div className="flex-1">
                        <h4 className="text-sm font-medium text-gray-900">{activity.title}</h4>
                        <p className="text-xs text-gray-600">{activity.description}</p>
                      </div>
                      <div className="text-xs text-gray-500">
                        {format(new Date(activity.date), 'MMM d')}
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="text-center py-8">
                <BarChart3 className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                <p className="text-gray-500">No recent activity</p>
              </div>
            )}
          </div>

          {/* Actions */}
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Account Actions</h3>
            <div className="space-y-3">
              <button
                onClick={() => window.location.href = '/journal'}
                className="w-full btn-outline flex items-center justify-center"
              >
                <BookOpen className="w-4 h-4 mr-2" />
                Write Journal Entry
              </button>

              <button
                onClick={() => window.location.href = '/stories'}
                className="w-full btn-outline flex items-center justify-center"
              >
                <MessageSquare className="w-4 h-4 mr-2" />
                View Stories
              </button>

              <button
                onClick={() => window.location.href = '/practice'}
                className="w-full btn-outline flex items-center justify-center"
              >
                <Play className="w-4 h-4 mr-2" />
                Practice Mode
              </button>

              <button
                onClick={handleLogout}
                className="w-full bg-red-600 hover:bg-red-700 text-white font-medium py-2 px-4 rounded-lg transition-colors duration-200 flex items-center justify-center"
              >
                <X className="w-4 h-4 mr-2" />
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
