import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { storiesAPI, practiceAPI } from '../services/api';
import { 
  Play, 
  Pause, 
  Mic, 
  MicOff, 
  Volume2, 
  VolumeX,
  MessageSquare,
  Star,
  Clock,
  Target,
  RefreshCw,
  ArrowRight,
  CheckCircle,
  AlertCircle
} from 'lucide-react';
import toast from 'react-hot-toast';

const Practice = () => {
  const [searchParams] = useSearchParams();
  const [stories, setStories] = useState([]);
  const [selectedStory, setSelectedStory] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [practiceMode, setPracticeMode] = useState('storytelling'); // storytelling, conversation, feedback
  const [userDelivery, setUserDelivery] = useState('');
  const [feedback, setFeedback] = useState(null);
  const [gettingFeedback, setGettingFeedback] = useState(false);
  const [sessionId, setSessionId] = useState(null);

  useEffect(() => {
    loadStories();

    // Check if a specific story was requested
    const storyId = searchParams.get('story');
    if (storyId) {
      // Load the specific story
      loadSpecificStory(storyId);
    }
  }, [searchParams]);

  const loadStories = async () => {
    try {
      const response = await storiesAPI.getStories(1, 50);
      setStories(response.data.stories || []);
    } catch (error) {
      console.error('Error loading stories:', error);
      toast.error('Failed to load stories');
    } finally {
      setLoading(false);
    }
  };

  const loadSpecificStory = async (storyId) => {
    try {
      const response = await storiesAPI.getStory(storyId);
      setSelectedStory(response.data.story);
    } catch (error) {
      console.error('Error loading specific story:', error);
      toast.error('Failed to load story');
    }
  };

  const startPracticeSession = async () => {
    if (!selectedStory) return;

    try {
      const response = await practiceAPI.startSession({
        story_id: selectedStory.id,
        session_type: practiceMode
      });
      setSessionId(response.data.session_id);
      toast.success('Practice session started!');
    } catch (error) {
      console.error('Error starting practice session:', error);
      toast.error('Failed to start practice session');
    }
  };

  const handleGetFeedback = async () => {
    if (!userDelivery.trim() || !selectedStory) {
      toast.error('Please record or type your delivery first');
      return;
    }

    setGettingFeedback(true);
    try {
      const response = await practiceAPI.getFeedback({
        story_content: selectedStory.content,
        user_delivery: userDelivery,
        session_id: sessionId
      });

      setFeedback(response.data.feedback);
      toast.success('Feedback generated!');
    } catch (error) {
      console.error('Error getting feedback:', error);
      toast.error('Failed to get feedback');
    } finally {
      setGettingFeedback(false);
    }
  };

  const handleStartRecording = () => {
    setIsRecording(true);
    // In a real app, you would implement actual voice recording here
    toast('Recording started (demo mode)', { icon: 'ðŸŽ¤' });
  };

  const handleStopRecording = () => {
    setIsRecording(false);
    // In a real app, you would process the recorded audio here
    setUserDelivery("This is a demo delivery. In the real app, this would be your actual spoken story.");
    toast('Recording completed (demo mode)', { icon: 'âœ…' });
  };

  const handlePlayStory = () => {
    setIsPlaying(true);
    // In a real app, you would implement text-to-speech here
    toast('Playing story (demo mode)', { icon: 'ðŸ”Š' });
    setTimeout(() => setIsPlaying(false), 3000);
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-6"></div>
          <div className="h-64 bg-gray-200 rounded-lg"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Practice Mode</h1>
        <p className="text-gray-600 mt-1">
          Rehearse your stories and get AI feedback to improve your delivery
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Story Selection */}
        <div className="lg:col-span-1">
          <div className="card">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Select a Story</h2>

            {stories.length > 0 ? (
              <div className="space-y-3">
                {stories.map((story) => (
                  <button
                    key={story.id}
                    onClick={() => setSelectedStory(story)}
                    className={`w-full text-left p-3 rounded-lg border transition-colors duration-200 ${
                      selectedStory?.id === story.id
                        ? 'border-primary-500 bg-primary-50'
                        : 'border-gray-200 hover:border-gray-300 hover:bg-gray-50'
                    }`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <h3 className="font-medium text-gray-900 text-sm">{story.title}</h3>
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        story.tone === 'funny' ? 'bg-yellow-100 text-yellow-800' :
                        story.tone === 'thoughtful' ? 'bg-blue-100 text-blue-800' :
                        story.tone === 'casual' ? 'bg-green-100 text-green-800' :
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {story.tone}
                      </span>
                    </div>
                    <p className="text-xs text-gray-600 line-clamp-2">{story.content}</p>
                    <div className="flex items-center mt-2 text-xs text-gray-500">
                      <Clock className="w-3 h-3 mr-1" />
                      {story.duration_seconds}s
                      {story.times_told > 0 && (
                        <>
                          <span className="mx-2">â€¢</span>
                          <Target className="w-3 h-3 mr-1" />
                          {story.times_told} times
                        </>
                      )}
                    </div>
                  </button>
                ))}
              </div>
            ) : (
              <div className="text-center py-8">
                <MessageSquare className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                <p className="text-gray-500 mb-4">No stories available</p>
                <button
                  onClick={() => window.location.href = '/stories'}
                  className="btn-primary text-sm"
                >
                  Create Stories
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Practice Area */}
        <div className="lg:col-span-2">
          {selectedStory ? (
            <div className="space-y-6">
              {/* Story Display */}
              <div className="card">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-gray-900">{selectedStory.title}</h2>
                  <div className="flex items-center space-x-2">
                    <span className={`px-2 py-1 text-xs rounded-full ${
                      selectedStory.tone === 'funny' ? 'bg-yellow-100 text-yellow-800' :
                      selectedStory.tone === 'thoughtful' ? 'bg-blue-100 text-blue-800' :
                      selectedStory.tone === 'casual' ? 'bg-green-100 text-green-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {selectedStory.tone}
                    </span>
                    <span className="text-xs text-gray-500">
                      {selectedStory.duration_seconds}s
                    </span>
                  </div>
                </div>

                <div className="bg-gray-50 rounded-lg p-4 mb-4">
                  <p className="text-gray-900 leading-relaxed">{selectedStory.content}</p>
                </div>

                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <button
                      onClick={handlePlayStory}
                      disabled={isPlaying}
                      className="btn-secondary flex items-center text-sm"
                    >
                      {isPlaying ? (
                        <>
                          <Pause className="w-4 h-4 mr-2" />
                          Playing...
                        </>
                      ) : (
                        <>
                          <Volume2 className="w-4 h-4 mr-2" />
                          Play Story
                        </>
                      )}
                    </button>

                    <button
                      onClick={() => window.location.href = `/stories`}
                      className="btn-outline text-sm flex items-center"
                    >
                      <RefreshCw className="w-4 h-4 mr-2" />
                      Change Story
                    </button>
                  </div>

                  <button
                    onClick={startPracticeSession}
                    className="btn-primary text-sm flex items-center"
                  >
                    <Play className="w-4 h-4 mr-2" />
                    Start Practice
                  </button>
                </div>
              </div>

              {/* Practice Mode Selection */}
              <div className="card">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Practice Mode</h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  {[
                    { id: 'storytelling', name: 'Storytelling', desc: 'Practice telling the story' },
                    { id: 'conversation', name: 'Conversation', desc: 'Practice in conversation context' },
                    { id: 'feedback', name: 'Get Feedback', desc: 'Record and get AI feedback' }
                  ].map((mode) => (
                    <button
                      key={mode.id}
                      onClick={() => setPracticeMode(mode.id)}
                      className={`p-4 rounded-lg border text-left transition-colors duration-200 ${
                        practiceMode === mode.id
                          ? 'border-primary-500 bg-primary-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                    >
                      <h4 className="font-medium text-gray-900">{mode.name}</h4>
                      <p className="text-sm text-gray-600 mt-1">{mode.desc}</p>
                    </button>
                  ))}
                </div>
              </div>

              {/* Recording/Delivery Area */}
              <div className="card">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Your Delivery</h3>

                {practiceMode === 'feedback' ? (
                  <div className="space-y-4">
                    <div className="flex items-center justify-center space-x-4">
                      <button
                        onClick={isRecording ? handleStopRecording : handleStartRecording}
                        className={`p-4 rounded-full transition-colors duration-200 ${
                          isRecording 
                            ? 'bg-red-500 hover:bg-red-600 text-white' 
                            : 'bg-primary-500 hover:bg-primary-600 text-white'
                        }`}
                      >
                        {isRecording ? (
                          <MicOff className="w-6 h-6" />
                        ) : (
                          <Mic className="w-6 h-6" />
                        )}
                      </button>

                      <div className="text-center">
                        <p className="text-sm text-gray-600">
                          {isRecording ? 'Recording... Click to stop' : 'Click to start recording'}
                        </p>
                      </div>
                    </div>

                    <div>
                      <label htmlFor="delivery" className="block text-sm font-medium text-gray-700 mb-2">
                        Or type your delivery:
                      </label>
                      <textarea
                        id="delivery"
                        value={userDelivery}
                        onChange={(e) => setUserDelivery(e.target.value)}
                        rows={4}
                        className="input-field"
                        placeholder="Type how you would tell this story..."
                      />
                    </div>

                    <button
                      onClick={handleGetFeedback}
                      disabled={gettingFeedback || !userDelivery.trim()}
                      className="btn-primary w-full flex items-center justify-center"
                    >
                      {gettingFeedback ? (
                        <>
                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                          Getting Feedback...
                        </>
                      ) : (
                        <>
                          <Star className="w-4 h-4 mr-2" />
                          Get AI Feedback
                        </>
                      )}
                    </button>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                      <Play className="w-8 h-8 text-primary-600" />
                    </div>
                    <h4 className="text-lg font-medium text-gray-900 mb-2">
                      {practiceMode === 'storytelling' ? 'Practice Telling' : 'Conversation Practice'}
                    </h4>
                    <p className="text-gray-600 mb-4">
                      {practiceMode === 'storytelling' 
                        ? 'Practice telling this story out loud. Focus on timing, tone, and engagement.'
                        : 'Practice working this story into a natural conversation flow.'
                      }
                    </p>
                    <button
                      onClick={startPracticeSession}
                      className="btn-primary flex items-center mx-auto"
                    >
                      <Play className="w-4 h-4 mr-2" />
                      Start Practice
                    </button>
                  </div>
                )}
              </div>

              {/* Feedback Display */}
              {feedback && (
                <div className="card">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">AI Feedback</h3>

                  <div className="space-y-4">
                    <div className="flex items-center space-x-2">
                      <Star className="w-5 h-5 text-yellow-500" />
                      <span className="font-medium text-gray-900">
                        Overall Rating: {feedback.rating}/10
                      </span>
                    </div>

                    <div className="bg-gray-50 rounded-lg p-4">
                      <p className="text-gray-900">{feedback.feedback}</p>
                    </div>

                    {feedback.strengths && feedback.strengths.length > 0 && (
                      <div>
                        <h4 className="font-medium text-green-800 mb-2 flex items-center">
                          <CheckCircle className="w-4 h-4 mr-2" />
                          Strengths
                        </h4>
                        <ul className="list-disc list-inside space-y-1">
                          {feedback.strengths.map((strength, index) => (
                            <li key={index} className="text-green-700 text-sm">{strength}</li>
                          ))}
                        </ul>
                      </div>
                    )}

                    {feedback.improvements && feedback.improvements.length > 0 && (
                      <div>
                        <h4 className="font-medium text-blue-800 mb-2 flex items-center">
                          <AlertCircle className="w-4 h-4 mr-2" />
                          Areas for Improvement
                        </h4>
                        <ul className="list-disc list-inside space-y-1">
                          {feedback.improvements.map((improvement, index) => (
                            <li key={index} className="text-blue-700 text-sm">{improvement}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          ) : (
            <div className="card text-center py-12">
              <MessageSquare className="w-16 h-16 text-gray-300 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">Select a Story to Practice</h3>
              <p className="text-gray-600 mb-6">
                Choose a story from the sidebar to start practicing your delivery
              </p>
              <button
                onClick={() => window.location.href = '/stories'}
                className="btn-primary flex items-center mx-auto"
              >
                <ArrowRight className="w-4 h-4 mr-2" />
                Browse Stories
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Practice;
